mapper.insert:

		  	INSERT INTO vd_type
                (
                    id
                    , vd_type
                    , remark
                    , create_at
                    , create_by
                    , status
                )
                VALUES(
                    #{id}
                    , #{vdType}
                    , #{remark}
                    , #{createAt}
                    , #{userId}
                    , #{status}
                );

mapper.update:

UPDATE vd_type
SET
    vd_type     = #{vdType}
    , remark    = #{remark}
    , modify_at = #{modifyAt}
    , modify_by = #{userId}
    , status    = #{status}
WHERE id        = #{id};


UPDATE vd_type
SET
    status      = ${status}
    , modify_at = #{modifyAt}
    , modify_by = #{userId}
WHERE id        = #{id};
