mapper.insert:

INSERT INTO user_
    (
        id
        , account_expired
        , account_locked
        , credentials_expired
        , enabled
        , "password"
        , user_name
        , status
        , create_date
        , create_by
        , full_name
        , gender
        , date_birth
        , phone_number
        , resource_id
        , is_first_login
        , remark
        , address
        , role_id
    )
VALUES
    (
     #{id}
    , #{accountExpired}
    , #{accountLocked}
    , #{credentialsExpired}
    , #{enabled}
    , #{password}
    , #{userName}
    , #{status}
    , #{createAt}
    , #{userId}
    , #{fullName}
    , #{gender}
    , #{dateBirth}
    , #{phoneNumber}
    , #{resourceId}
    , #{isFirstLogin}
    , #{remark}
    , #{address}
    , #{roleId}
);

mapper.update:

            UPDATE user_
			SET
			    full_name       = #{fullName}
			    , gender        = #{gender}
			    , date_birth    = #{dateBirth}
			    , phone_number  = #{phoneNumber}
			    , user_name     = #{userName}
			    , address       = #{address}
			    , role_id       = #{roleId}
			    , resource_id   = #{resourceId}
			    , status        = #{status}
			    , modify_date   = #{modifyAt}
			    , modify_by     = #{userId}
			WHERE id 	= #{id}


            UPDATE user_
			SET
				password 	        = #{password}
				, is_first_login    = #{isFirstLogin}
			WHERE id 	= #{id} and user_name = #{userName}


            UPDATE user_
			SET
				modify_date = #{modifyAt}
				, status    = #{status}
			WHERE id 	    = #{userId}


            UPDATE user_
			SET
				modify_date = #{modifyAt}
				, modify_by     = #{userId}
				, status    = #{status}
				, enabled    = #{enable}
			WHERE id 	    = #{userId}


            UPDATE user_
			SET
				modify_date = #{modifyAt}
				, modify_by = #{userId}
				, status    = #{status}
				, password  = #{password}
			WHERE id 	    = #{userId}
